class SocialIconBar extends HTMLElement{constructor(){super()}static get observedAttributes(){return["icons"]}attributeChangedCallback(name,oldValue,newValue){"icons"===name&&(this.icons=newValue,this.render())}connectedCallback(){this.render()}render(){if(!this.icons)return;const _icons=JSON.parse(this.icons);this.innerHTML=`\n            <style>\n                .social-icon-bar {\n                    width: 100%;\n                    background-color: ${this.backgroundColor||"black"};\n                    display: flex;\n                    justify-content: center;\n                    flex-direction: row;\n                    gap: 1rem;\n                }\n\n                .social-icon-bar__icon {\n                    color: white;\n                }\n            </style>\n            <div class="social-icon-bar">\n                ${_icons.map((icon=>`\n                        <a href="${icon.url}" target="${icon.targetBlank?"_blank":""}" class="social-icon-bar__icon">\n                            <img src="https://api.iconify.design/bxl/${icon.site}.svg" alt="${icon.site}" />\n                        </a>\n                    `)).join("")}\n            </div>\n        `}}customElements.define("social-icon-bar",SocialIconBar);